function setStories( stories ) {
	return {
		type: types.SET_STORIES,
		payload: {
			stories: stories
		}
	};
}

function setStoryBeingRead( storyId, allStories ) {
	return {
		type: types.SET_STORY_BEING_READ,
		payload: {
			storyIdToRead: storyId,
			stories: allStories
		}
	};
}

function setNextChapter( chapter ) {
	return {
		type: types.SET_CHAPTER,
		payload: {
			chapter: chapter
		}
	};
}